                    if(movement != 0.0) {
                        entityDb.insert(
                            CashMovement(
                                amount = movement,
                                currency = instrumentCurrency[it.instrumentId]!!,
                                movementDate = now(),
                                participantName = it.sellParticipantName,
                                reason = Reason.VariationMargin,
                                tradeId = it.tradeId
                            )
                        )
                    }
                }
